import { defineStore } from "pinia";
import AuthService from "@/services/account.service";
import CartService from "@/services/cart.service";

export const useAppStore = defineStore("auth", {
  state() {
    return {
      user: null,
      SearchText: "",
      role: "",
    };
  },
  getters: {
    isUserLoggedIn(state) {
      return !!state.user && !!state.user.accessToken;
    },
  },
  actions: {
    formatter(data) {
      const fm = new Intl.NumberFormat("vi-VN", {
        style: "currency",
        currency: "VND",
        minimumFractionDigits: 0,
      });
      data = fm.format(data);
      return data;
    },
    loadAuthState() {
      this.user = JSON.parse(localStorage.getItem("user"));
      if (this.user) {
        this.role = this.user.role;
      }
      this.SearchText = "";
    },
    logout() {
      this.SearchText = "";
      this.role = "";
      this.user = null;
      localStorage.removeItem("user");
    },
    async login(user) {
      const response = await AuthService.login(user);

      if (!response.accessToken) {
        this.logout();
        throw new Error("Whoops, no access token found!");
      }

      this.user = response;
      if (this.user) {
        this.role = this.user.role;
      }

      localStorage.setItem("user", JSON.stringify(response));

      return response;
    },
    register(user) {
      this.user = null;
      return AuthService.register(user);
    },
    async getCartStore() {
      await CartService.getCart();
    },
  },
});
