// action="/courses/handle-form-actions"
<template>
  <form class="mt-4" name="container-form">
    <div class="mt-4 d-flex align-item-center tm">
      <div class="form-check">
        <input
          @click="renderCheckAllSubmitBtn"
          @change="changeCheckbox"
          type="checkbox"
          class="form-check-input"
          value=""
          id="checkboxAll"
          v-model="checked"
        />
        <label class="form-check-label" for="checkboxAll">
          Chọn tất cả
          {{ checked }}
        </label>
      </div>
      <select
        class="form-select form-select-sm checkbox-select-all-options"
        name="action"
      >
        <option value="">Chọn hành động</option>
        <option value="delete">Xóa</option>
      </select>

      <button
        ref="checkAllSubmitBnt"
        class="btn btn-primary btn-sm check-all-submit-btn"
        @click="t"
      >
        Thực hiện
      </button>
    </div>
    <table class="table">
      <thead>
        <div>
          <th scope="col" colspan="2">#</th>
          <th scope="col">Sản phẩm</th>
          <th scope="col">Kho</th>
          <th scope="col" colspan="2">Giá</th>
        </div>
      </thead>
      <tbody>
        <div v-if="filteredProductsCount > 0">
          <tr v-for="(product, item) in filteredProducts" :key="item">
            <td>
              <div class="form-check">
                <input
                  type="checkbox"
                  class="form-check-input"
                  courseItemCheckbox="courseIds[]"
                  ref="checkItem"
                  name="courseIds[]"
                  value="{{this._id}}"
                  v-model="checked"
                />
              </div>
            </td>
            <th scope="row"></th>
            <td>{{ product.name }}</td>
            <td>{{ product.quantity }}</td>
            <td>{{ formatter(`${product.BaPrice}`) }}</td>
            <td>{{ formatter(`${product.PrPrice}`) }}</td>
            <td>
              <router-link
                :to="{
                  path: '/admin/`${product.BaPrice}`/edit',
                  params: { id: product._id },
                }"
                class="btn btn-link"
              >
                Sửa
              </router-link>
            </td>
            <td>
              <button @click="deleteProduct(`${product._id}`)">Xóa</button>
            </td>
          </tr>
        </div>
        <div v-else>
          <tr>
            <td colspan="5" class="text-center">
              Bạn chưa có khóa học nào.
              <a href="/courses/create">Tạo khóa học</a>
            </td>
          </tr>
        </div>
      </tbody>
    </table>
  </form>

  <!-- {{!-- comfirm delete course--}} -->
  <!-- <div class="modal fade" id="delete-cousre-Modal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title" id="exampleModalLabel">Xóa vĩnh viễn khóa học</h5>
        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
      <div class="modal-body">
        Bạn chắc chắn muốn xóa khóa học này vĩnh viễn?
      </div>
      <div class="modal-footer">
        <button id = "btn-delete-course" type="button" class="btn btn-danger">Xóa vĩnh viễn</button>
        <button type="button" class="btn btn-secondary"  data-bs-dismiss="modal">Hủy</button>
      </div>
    </div>
</div> -->

