<form name="restore-course-form" method="POST"></form> -->
</template>

<script>
import ContactService from "@/services/contact.service";

export default {
  data() {
    return {
      products: [],
      checked: false,
      courseItemCheckbox: [],
    };
  },
  computed: {
    filteredProducts() {
      return this.products;
    },
    filteredProductsCount() {
      return this.filteredProducts.length;
    },
  },
  methods: {
    t() {
      console.log(this.mountproduct);
    },
    formatter(data) {
      const fm = new Intl.NumberFormat("vi-VN", {
        style: "currency",
        currency: "VND",
        minimumFractionDigits: 0,
      });
      data = fm.format(data);
      return data;
    },
    async deleteProduct(data) {
      try {
        await ContactService.forceDelete(data);
      } catch (error) {
        console.log(error);
      }
    },
    async retrieveProducts() {
      try {
        this.products = await ContactService.getAll();
      } catch (error) {
        console.log(error);
      }
    },
    renderCheckAllSubmitBtn() {
      console.log(this.courseItemCheckbox);
      // checkedCount > 0
      //   ? this.$refs.checkAllSubmitBnt.classList.remove("disabled")
      //   : this.$refs.checkAllSubmitBnt.classList.add("disabled");
    },
    // changeCheckbox() {
    //   let isCheckedAll = this.checked;

    //   this.courseItemCheckbox.forEach((Item) => {
    //     Item.checked = isCheckedAll;
    //   });
    //   // renderCheckAllSubmitBtn();
    // },
  },
  mounted() {
    this.retrieveProducts();
  },
};
</script>

