import { createRouter, createWebHistory } from "vue-router";
import { useProductData } from "@/stores/Products";

import NotFound from "../views/NotFound.vue";
import HomeView from "../views/HomeView.vue";
import AboutView from "../views/AboutView.vue";

import LoginView from "../views/LoginView.vue";
import RegisterView from "../views/RegisterView.vue";

import CardView from "../views/CardView.vue";

import DetailProductView from "../views/DetailProductView.vue";
import ProductView from "../views/ProductView.vue";
import SearchView from "../views/SearchView.vue";

import AdminView from "../views/AdminView.vue";
import AdminAdd from "../components/AdminAdd.vue";
import AdminAllProduct from "../components/AdminAllProduct.vue";
import AdminEditProduct from "../components/AdminEditProduct.vue";

const routes = [
  {
    path: "/",
    name: "home",
    component: HomeView,
  },
  {
    path: "/productdetail/:id",
    name: "productdetail",
    component: DetailProductView,
    props: true,
  },
  {
    path: "/cart",
    name: "cart",
    component: CardView,
    props: true,
  },
  {
    path: "/login",
    name: "login",
    component: LoginView,
  },
  {
    path: "/register",
    name: "register",
    component: RegisterView,
  },
  {
    path: "/about",
    name: "about",
    component: AboutView,
  },
  {
    path: "/product",
    name: "product",
    component: ProductView,
  },
  {
    path: "/search",
    name: "SearchView",
    component: SearchView,
    props: true,
  },
  {
    path: "/admin",
    name: "admin",
    component: AdminView,
    children: [
      {
        path: "/admin/allProduct",
        name: "AdminAllProduct",
        component: AdminAllProduct,
      },
      {
        path: "/admin/addProduct",
        name: "AdminAdd",
        component: AdminAdd,
      },
      {
        path: "/admin/:id/edit",
        name: "AdminEditProduct",
        component: AdminEditProduct,
        props: true,
      },
    ],
  },
  // {
  //   path: "/admin/addProduct",
  //   name: "addProduct",
  //   component: AdminAdd,
  // },
  {
    path: "/:pathMatch(.*)*",
    name: "notfound",
    component: NotFound,
  },
];

const store = useProductData();

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes,
});

router.beforeEach((to, from, next) => {
  // we wanted to use the store here
  if (store.isLoggedIn) next();
  else next("/login");
});

router.beforeEach((to) => {
  const store = useProductData();

  if (to.meta.requiresAuth && !store.isLoggedIn) return "/login";
});
export default router;
