import { defineStore } from "pinia";
import AccountService from "@/services/account.service";

export const useProductData = defineStore("product", {
  //data
  state: () => {
    return {
      SearchText: "",
      dataUsers: {},
    };
  },
  actions: {
    async Login(data) {
      try {
        const s = await AccountService.loginAccount(data);
        if (s.message === "Login successful") {
          // console.log(s);
          this.dataUsers = s;
          await this.getUsetName(s);
          return s;
        } else {
          return false;
        }
      } catch (error) {
        console.log(error);
      }
    },
    getUsetName() {
      // const user = this.Login(data);
      return this.Login.value;
    },
    //   async Logout() {
    //     (this.dataUsers = " "), await AccountService.logoutAccount();
    //   },
  },
});
