import { defineStore } from "pinia";
import ProductService from "@/services/product.service";

export const useProductStore = defineStore("product", {
  state: () => {
    return {
      SearchText: "",
      dataUsers: {},
      products: [],
    };
  },
  actions: {
    async retrieveProducts() {
      try {
        this.products = await ProductService.getAll();
      } catch (error) {
        console.log(error);
      }
    },
  },
});
